mapper.select:
SELECT id, seller_id, name, price, stock, status, created_at
FROM products
WHERE id = #{id}

SELECT id, seller_id, name, price, stock, status, created_at FROM products
AND (name ILIKE CONCAT('%', #{keyword}, '%'))
AND price >= #{minPrice}
AND price <= #{maxPrice}
ORDER BY created_at ASC ORDER BY created_at DESC ORDER BY price ASC ORDER BY price DESC ORDER BY created_at DESC
LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE products
SET name = #{name}, price = #{price}, stock = #{stock}, status = #{status}
WHERE id = #{id}

mapper.select:
SELECT CASE WHEN COUNT(1) > 0 THEN TRUE ELSE FALSE END
FROM products
WHERE id = #{productId} AND seller_id = #{sellerId}

mapper.update:
UPDATE products
SET stock = stock - #{quantity}
WHERE id = #{productId}
  AND stock >= #{quantity}
  AND status = 'ACTIVE'

UPDATE products
SET stock = stock + #{quantity}
WHERE id = #{productId}

mapper.select:
SELECT price
FROM products
WHERE id = #{productId}
